import React, {useState, useEffect, Fragment} from 'react';
import * as d3 from 'd3';
import Navbar from './components/layout/Navbar';
import Bargraph from './components/Charts/Bargraph';
import Piechart from './components/Charts/Piechart';
import './App.css';
import axios from 'axios';

function App () {
  const [data, setData] = useState({});

  useEffect(() => {
    getDataFromApi();
  }, []);

  const getDataFromApi= async () => {
    try {
        await axios.get("http://localhost:5000/api/fetchdataperyear").then((res)=>{
        setData(res.data.aggregations.group_by_year.buckets);
      });
    } catch (err) {
      console.log(err);
    }
  }

  var arrdata = [];
  var counttype = [];
        data.forEach((d)=>{
        d.group_by_Type.buckets.forEach((bucket)=>{
        counttype.push({'total':bucket.total_count.value,'type':bucket.key});
    })  
      arrdata.push({
        "key" : d.key_as_string,
        "count" : counttype
      })
    })
    console.log(arrdata[0]);
return (
    <Fragment>
      < Navbar />
      <svg width="500" height="500">
      < Piechart fetcheddata={arrdata} x={250} y={250}/>
      </svg>
    </Fragment>
)};

export default App;
