import React, {useState, useEffect} from 'react'
import { PieChart, Pie, Legend, Tooltip, ResponsiveContainer } from 'recharts';
import * as d3 from 'd3';

const Piechart = ({fetcheddata}) => {
if({fetcheddata})console.log({fetcheddata});

  const arrdata = [];
  const counttype = [];
  if (fetcheddata.length) fetcheddata.forEach((d)=>{
        d.group_by_Type.buckets.forEach((bucket)=>{
        counttype.push({'total':bucket.total_count.value,'type':bucket.key});
    })  
      arrdata.push({
        "key" : d.key_as_string,
        "count" : counttype
      })
    })
    console.log(arrdata[0]);
    console.log(counttype);

 return !counttype.length ? (<div>Loading..</div>) :(
        <ResponsiveContainer width="100%" height="100%">
        <PieChart width={400} height={400}>
          <Pie
            dataKey="total"
            isAnimationActive={false}
            data={counttype}
            cx="50%"
            cy="50%"
            outerRadius={80}
            fill="#8884d8"
            label
          />
          <Tooltip />
        </PieChart>
      </ResponsiveContainer>   
    )
}

export default Piechart;
