import React, {useState, useEffect} from 'react'
import { PieChart, Pie, Legend, Tooltip, ResponsiveContainer,Cell } from 'recharts';


const Piechart = ({fetcheddata}) => {

 const [counttype, setData] = useState([]); 
 useEffect(()=>{
  const arrdata = [];
  const counttype = [];
  if (fetcheddata.length) fetcheddata.forEach((d)=>{
        d.group_by_Type.buckets.forEach((bucket)=>{
        counttype.push({'total':bucket.total_count.value,'type':bucket.key});
    })  
      arrdata.push({
        "key" : d.key_as_string,
        "count" : counttype
      })
    })
    setData(counttype);
 },[fetcheddata])
 
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];


 return (
        <ResponsiveContainer width={800} height={800}>
        <PieChart width={400} height={400}>
        <Pie
        data={counttype}
        cx={200}
        cy={200}
        outerRadius={80}
        fill="#8884d8"
        // dataKey="total"
        label dataKey="key"
      >
        {counttype.map((entry, index) => (
          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
        ))}
      </Pie>
        </PieChart>
      </ResponsiveContainer>   
    )
}

export default Piechart;
